const log = console.log;

const createCounter = () =&gt; {
  let count = 0; // —ç—Ç–∞ –ø–µ—Ä–µ–º–µ–Ω–Ω–∞—è –æ—Å—Ç–∞—ë—Ç—Å—è –¥–æ—Å—Ç—É–ø–Ω–æ–π –≤–æ –≤–Ω—É—Ç—Ä–µ–Ω–Ω–µ–π —Ñ—É–Ω–∫—Ü–∏–∏
  return () =&gt; {
